import React, { useState } from "react";

const Project = ({ id, screenshot, name, category, info, url, github }) => {
  console.log("test");
  const [readMore, setReadMore] = useState(false);
  return (

    <>
      <article className="single-project">
        <p>{id}</p>
        <img className="screenshot" src={screenshot} alt={name} />

        <footer>
          <div className="project-info">
            <h4>{name}</h4>
            <h4 className="project-cat">{category}</h4>
          </div>
          <p>
          {readMore ? info : `${info.substring(0, 200)}... `}
          <button onClick={() => setReadMore(!readMore)}>
            {readMore ? " show less" : "show more"}
          </button>
          <a href="http://"></a>
        </p>
        <div className="project-link">
        <a className= "link-btn" type="button" href={'http://google.com'}>Google</a>
        
          <button type="button" className="link-btn" onClick={{url}}>See Project</button>
          <button className = "github-btn" href={github}>Git-Hub-Repository</button>
        </div>
        </footer>
      </article>
    </>

  );
};

export default Project;
